                depId = i.DepartmetUnitPossitionsId;
            }

            foreach (var j in taskAllocations.Where(u => u.StatusId == 1 && u.CreatedUser == user))
            {
                foreach (var i in taskAllocationDetails.Where(u => u.TaskAllocationId == j.TaskAllocationId))
                {
                    myList.Add(i);
                }
            }
            StateHasChanged();
        }
        
    }


